import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const app = props => {
  const [ personsState, setPersonsState ] =  useState({
      person : [
        {name:'Andres', age:25 },
        {name:'Paula', age:24 },
        {name:'Sthepanie', age:26 }
      ],
      otherSatet : 'algo mas'
    });
    return (
      <div className="App">
        <h1> App usando react </h1>
        <p> Eto realmente funciona </p>
        <button onClick={this.clicFunctionHandler}> Click button </button>
        <Person name={personsState.person[0].name} age={personsState.person[0].age} />
        <Person name={personsState.person[1].name} age={personsState.person[1].age} />
        <Person name={personsState.person[2].name} age={personsState.person[2].age} />
      </div>
    );
}

export default app;

state = {
  person : [
    {name:'Andres', age:25 },
    {name:'Paula', age:24 },
    {name:'Sthepanie', age:26 }
  ]
}
clicFunctionHandler = () =>{
  console.log('Le dio click papu');
  this.setState( {
    person : [
      {name:'Edgar Andres', age:25 },
      {name:'Paula', age:24 },
      {name:'Sthepanie', age:27 }
    ]
  } )
}
