import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    person : [
      {name:'Andres', age:25 },
      {name:'Paula', age:24 },
      {name:'Sthepanie', age:26 }
    ]
  }
  clicFunctionHandler = () =>{
    console.log('Le dio click papu');
    this.setState( {
      person : [
        {name:'Edgar Andres', age:25 },
        {name:'Paula', age:24 },
        {name:'Sthepanie', age:27 }
      ]
    } )
  }
  render() {
    return (
      <div className="App">
        <h1> App usando react </h1>
        <p> Eto realmente funciona </p>
        <button onClick={this.clicFunctionHandler}> Click button </button>
        <Person 
        name={this.state.person[0].name} 
        age={this.state.person[0].age} />
        <Person 
        name={this.state.person[1].name} 
        age={this.state.person[1].age}>Mi hobbi es leer fap ceo</Person>
        <Person 
        name={this.state.person[2].name} 
        age={this.state.person[2].age} />
      </div>
    );
  }
}

export default App;
