import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    person : [
      { id:'asd1', name:'Andres', age:25 },
      { id:'qee1', name:'Paula', age:24 },
      { id:'hgf1', name:'Sthepanie', age:26 }
    ],
    showPerson : false
  }
  clicFunctionHandler = (newName) =>{
    console.log('Le dio click papu');
    this.setState( {
      person : [
        { name:newName, age:25 },
        { name:'Paula', age:24 },
        { name:'Sthepanie', age:27 }
      ]
    } )
  }
  changedHandler = ( event,id ) => {
    const personIndex = this.state.person.findIndex(p =>{
      return p.id === id;
    } );
    const persona = {
      ...this.state.person[personIndex]
    };
    persona.name = event.target.value;
    const person = [...this.state.person]
    this.setState( {
      person : [
        {name:'Andres', age:25 },
        {name:event.target.value, age:24 },
        {name:'Sthepanie', age:27 }
      ]
    } )
  }
  togglePersonHandler = () =>{
    const doesShow = this.state.showPerson;
    this.setState({showPerson: !doesShow});
  }
  deletePersonHandler = (personIndes) => {
    //const persons =  this.state.person.slice();
    const persons =  [...this.state.person];
    persons.splice(personIndes,1);
    this.setState({person:persons})
  }
  render() {
    let persons = null;
    if (this.state.showPerson){
      persons = (
        <div>
          {this.state.person.map((personas, index) => {
            return <Person 
                    click={() => this.deletePersonHandler(index)}
                    name={personas.name} 
                    age={personas.age}
                    key={personas.id}
                    changed={(event) => this.changedHandler(event,personas.id)} />
          })}
        </div>
      )
    }
    return (
      <div className="App">
        <h1> App usando react </h1>
        <p> Esto realmente funciona </p>
        <button onClick={this.togglePersonHandler}> Click button </button>
        {persons}
      </div>
    );
  }
}

export default App;
